        self.tabWidget.setTabText(self.tabWidget.indexOf(self.tab_5), _translate("MainWindow", "System"))
        self.label_11.setToolTip(_translate("MainWindow", "<html><head/><body><pre style=\" margin-top:12px; margin-bottom:0px; margin-left:40px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\"> Available options are:</span></pre><pre style=\" margin-top:0px; margin-bottom:-13px; margin-left:40px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\"/></pre><p><span style=\" font-family:\'Courier New\';\">\'david\' :</span></p><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">Davidson iterative diagonalization with overlap matrix</span></pre><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">(default). Fast, may in some rare cases fail.</span></pre><pre style=\" margin-top:0px; margin-bottom:-13px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\"/></pre><p><span style=\" font-family:\'Courier New\';\">\'cg\' :</span></p><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">Conjugate-gradient-like band-by-band diagonalization.</span></pre><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">MUCH slower than \'david\' but uses less memory and is</span></pre><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">(a little bit) more robust.</span></pre><pre style=\" margin-top:0px; margin-bottom:-13px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\"/></pre><p><span style=\" font-family:\'Courier New\';\">\'ppcg\' :</span></p><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">PPCG iterative diagonalization</span></pre><pre style=\" margin-top:0px; margin-bottom:-13px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\"/></pre><p><span style=\" font-family:\'Courier New\';\">\'paro\', \'ParO\' :</span></p><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">ParO iterative diagonalization</span></pre><pre style=\" margin-top:0px; margin-bottom:-13px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\"/></pre><p><span style=\" font-family:\'Courier New\';\">\'rmm-davidson\', \'rmm-paro\' :</span></p><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">RMM-DIIS iterative diagonalization.</span></pre><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">To stabilize the SCF loop</span></pre><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">RMM-DIIS is alternated with calls to Davidson or</span></pre><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">ParO  solvers depending on the string used.</span></pre><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">Other variables that can be used to tune the behavior of</span></pre><pre style=\" margin-top:0px; margin-bottom:0px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\">RMM-DIIS are:  </span><a href=\"https://www.quantum-espresso.org/Doc/INPUT_PW.html#diago_rmm_ndim\"><span style=\" font-family:\'Courier New\'; text-decoration: underline; color:#0000ff;\">diago_rmm_ndim</span></a><span style=\" font-family:\'Courier New\';\"> and </span><a href=\"https://www.quantum-espresso.org/Doc/INPUT_PW.html#diago_rmm_conv\"><span style=\" font-family:\'Courier New\'; text-decoration: underline; color:#0000ff;\">diago_rmm_conv</span></a></pre><pre style=\" margin-top:0px; margin-bottom:-13px; margin-left:90px; margin-right:40px; -qt-block-indent:0; text-indent:0px;\"><span style=\" font-family:\'Courier New\';\"/></pre></body></html>"))
